import { useState, useEffect } from "react";
import { Card } from "./card";
import { Button } from "./button";
import { NotificationDropdown } from "./NotificationDropdown";
import { User, PlusCircle, Trash } from "lucide-react";

const logs = [
  {
    id: 1,
    service: "Auth Service",
    message: "User logged in",
    level: "info",
    time: "12:05 PM",
  },
  {
    id: 2,
    service: "Payment Service",
    message: "Transaction failed",
    level: "error",
    time: "12:10 PM",
  },
  {
    id: 3,
    service: "API Gateway",
    message: "Rate limit exceeded",
    level: "warning",
    time: "12:15 PM",
  },
  {
    id: 4,
    service: "Database",
    message: "Query executed",
    level: "info",
    time: "12:20 PM",
  },
];

export default function App() {
  const [notifications, setNotifications] = useState([]);
  const [selectedApp, setSelectedApp] = useState(null);
  const [limit, setLimit] = useState(10);
  const [timeFrame, setTimeFrame] = useState("24h");
  const [logType, setLogType] = useState("all");
  const [apps, setApps] = useState([
    "FaceBook Automation",
    "Payment Service",
    "HubSpot Automation",
    "Website",
  ]);
  const [newApp, setNewApp] = useState("");

  const addApp = () => {
    if (newApp.trim() && !apps.includes(newApp)) {
      setApps([...apps, newApp.trim()]);
      setNewApp("");
    }
  };

  const removeApp = (app) => {
    setApps(apps.filter((a) => a !== app));
    if (selectedApp === app) {
      setSelectedApp(null);
    }
  };
  useEffect(() => {
    const errorLogs = logs.filter((log) => log.level === "error");
    setNotifications(errorLogs);
  }, []);

  const filteredLogs = logs
    .filter((log) => logType === "all" || log.level === logType)
    .slice(0, limit);

  return (
    <div className="flex flex-col md:flex-row h-screen">
      {/* Sidebar */}
      <div className="w-full md:w-64 bg-gray-100 text-gray-900 p-4 space-y-4 rounded-r-lg shadow-lg border-r border-gray-300">
        <h2 className="text-xl font-bold border-b border-gray-400 pb-2">
          Apps
        </h2>
        <div className="space-y-2">
          {apps.map((app) => (
            <div
              key={app}
              className="flex items-center justify-between bg-white hover:bg-gray-200 p-3 rounded-lg shadow-sm"
            >
              <Button
                variant="ghost"
                className="w-full text-left"
                onClick={() => setSelectedApp(app)}
              >
                {app}
              </Button>
              <button
                onClick={() => removeApp(app)}
                className="text-red-500 hover:text-red-700 ml-2"
              >
                <Trash className="w-4 h-4" />
              </button>
            </div>
          ))}
        </div>
        <div className="flex items-center mt-4">
          <input
            type="text"
            value={newApp}
            onChange={(e) => setNewApp(e.target.value)}
            placeholder="New App Name"
            className="flex-1 p-2 border rounded-md w-4"
          />
          <button
            onClick={addApp}
            className="ml-2 text-green-500 hover:text-green-700"
          >
            <PlusCircle className="w-6 h-6" />
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-4 md:p-6 space-y-4">
        {/* Top Menu */}
        <div className="flex flex-col md:flex-row justify-between items-center bg-gray-100 p-4 rounded-md shadow border border-gray-300">
          <div className="flex flex-col md:flex-row md:space-x-4 space-y-2 md:space-y-0">
            <label className="flex items-center">
              Limit:
              <input
                type="number"
                value={limit}
                onChange={(e) => setLimit(Number(e.target.value))}
                className="ml-2 p-1 border rounded-md w-20"
              />
            </label>
            <label className="flex items-center">
              Time Frame:
              <select
                value={timeFrame}
                onChange={(e) => setTimeFrame(e.target.value)}
                className="ml-2 p-1 border rounded-md"
              >
                <option value="1h">Last 1 Hour</option>
                <option value="24h">Last 24 Hours</option>
                <option value="7d">Last 7 Days</option>
              </select>
            </label>
            <label className="flex items-center">
              Log Type:
              <select
                value={logType}
                onChange={(e) => setLogType(e.target.value)}
                className="ml-2 p-1 border rounded-md"
              >
                <option value="all">ALL</option>
                <option value="info">INFO</option>
                <option value="error">ERROR</option>
                <option value="warning">WARNING</option>
              </select>
            </label>
          </div>
          <div className="flex items-center space-x-4">
            <NotificationDropdown
              notifications={notifications}
              setNotifications={setNotifications}
            />
            <User className="w-6 h-6 text-gray-700" />
          </div>
        </div>

        <h1 className="text-2xl font-bold">
          APPLICATION : {selectedApp && `- ${selectedApp}`}
        </h1>

        {/* Logs Section */}
        <Card className="p-6 bg-gray-50 rounded-md shadow-lg border border-gray-300">
          <h2 className="text-lg font-bold text-gray-700 mb-3">LOGS</h2>
          <div className="space-y-2">
            {filteredLogs.map((log) => (
              <p
                key={log.id}
                className={`p-2 rounded-md ${
                  log.level === "error" ? " text-red-700" : " text-black-700"
                } ${log.level === "warning" ? "text-yellow-70" : ""}`}
              >
                <strong>{log.level.toUpperCase()} : </strong>
                <span className="font-medium text-sm">{log.time} : </span>
                <span className="font-medium text-sm">{log.message}</span>
              </p>
            ))}
          </div>
        </Card>
      </div>
    </div>
  );
}
